<script>
import Icon from '@/components/Icon'
import { mapGetters, mapState } from 'vuex'
import LoadingButton from "@/components/LoadingButton"

export default {
  components: {
    LoadingButton,
    Icon,
  },
  async fetch() {
    const { data } = await this.$axios.get('api/signup');
    this.$store.commit('setOrders', data);
  },
  data() {
    return {
      openTab: 1,
      form: {
        level: 1,
        subject: null,
        duration: {
          duration: null,
          price: null
        },
        pages: 1,
      },
      formProfile: {
        first_name: null,
        last_name: null,
        profile_image: null,
        country: 'United States of America',
        type: 'Student',
        email: null,
        phone: null,
        emailNotifications: {
          onOrderComplete: true,
          offers: true
        },
        pushNotifications: {
          sameAsEmail: true,
          none: false
        },
        password: null,
        admin_key: null,
      },
      userMeta: null,
      loading: false,
      currentForm: '',
      passwordFieldType: false
    }
  },
  computed: {
    ...mapState(["storedata", "orders","countries","userTypes"]),
    fullName() {
      return this.$auth.user ? this.$auth.user.name : null;
    },
    duration() {
      return this.storedata.filter(el => el.id === this.form.level)[0].deadline;
    },
    subjects() {
      return this.storedata.filter(el => el.id === this.form.level)[0].subjects;
    },
    totalPrice() {
      return this.form.duration.price * this.form.pages
    },
    computedCountry() {
      return this.formProfile.country ? { flag: this.countries.find(x => x.name === this.formProfile.country).flag, callingCode: this.countries.find(x => x.name === this.formProfile.country).callingCode } : ''
    }
  },
  methods: {
    changer(e) {
      console.log(e)
    },
    changePasswordFieldType() {
      this.passwordFieldType = !this.passwordFieldType
    },
    async saveProfile(section) {
      if (this.loadMetaData()) {
        this.loading = true;
        this.currentForm = section;
        let uploadInstance = this.userMeta;
        try {
          if (section === 'personal') {
            console.log('uploading', this.userMeta.profile.profile_image[0]);
            let public_id = 'profile_photo_' + Math.random().toString(36).substr(2, 16);
            uploadInstance.profile.profile_image = await this.$cloudinary.upload(this.userMeta.profile.profile_image[0], {
              public_id: public_id,
              folder: "profile",
              upload_preset: 'ybfqkqyu'
            });
          }
        } catch (e) {
          console.log(e);
        }
        try {
          let payload = {
            name: uploadInstance.personalInformation.first_name + ' ' + uploadInstance.personalInformation.last_name,
            email: uploadInstance.personalInformation.email,
            password: this.formProfile.password,
            phone: uploadInstance.personalInformation.phone,
            admin_key: this.formProfile.admin_key,
            owner: !!(this.formProfile.admin_key),
            metadata: {
              ...uploadInstance.profile,
              ...uploadInstance.notifications,
              country: this.formProfile.country
            }
          };

          console.log(payload);
          const response = await this.$axios.put('api/users?data=' + section, {
            ...payload
          });
          console.log('response', response);
          this.$toast.success(section + ' Information Updated', {
            theme: 'outline',
            position: 'bottom-right',
            duration: 5000
          });

          this.loading = false;
        } catch (e) {
          this.loading = false;
          if (e.response && e.response.data.message) {
            this.$toast.error(e.response.data.message, {
              theme: "outline",
              position: "bottom-right",
              duration: 5000
            });
          } else {
            this.$toast.error("Network Error", {
              theme: "outline",
              position: "bottom-right",
              duration: 5000
            });
          }
        }
      }
    },
    loadMetaData() {
      this.userMeta = {
        profile: {
          profile_image: this.formProfile.profile_image,
          userType: this.formProfile.type
        },
        personalInformation: {
          first_name: this.formProfile.first_name,
          last_name: this.formProfile.last_name,
          email: this.formProfile.email,
          phone: "+" + this.computedCountry.callingCode + " " + this.formProfile.phone,
        },
        notifications: {
          emailNotifications: this.formProfile.emailNotifications,
          pushNotifications: this.formProfile.pushNotifications
        }
      }
      return true
    },
    async readFileUrl(input) {

      if (input.files && input.files[0]) {

        function getBase64(file) {
          const reader = new FileReader()
          return new Promise(resolve => {
            reader.onload = ev => {
              resolve(ev.target.result)
            }
            reader.readAsDataURL(file)
          })
        }

        const promises = [];

        for (let i = 0; i < input.files.length; i++) {
          promises.push(getBase64(input.files[i]));
        }

        return await Promise.all(promises);
      }

    },
    async fileChange(e) {
      let fileArr = await this.readFileUrl(e.target);
      this.formProfile.profile_image = fileArr;
      console.log(fileArr)
    },
    handleFileUploadClick() {
      let fileElem = document.getElementById("fileUploader");
      if (fileElem) {
        fileElem.click();
      }
    },
    setFirstDuration() {
      this.form.duration = {
        duration: Object.entries(this.duration)[0][0],
        price: Object.entries(this.duration)[0][1] * this.form.pages,
      };
    },
    toggleTabs(tabNumber) {
      console.log({ tabNumber });
      this.openTab = tabNumber;
    },
    deleteOrder(id) {
      if (confirm('Are you sure you want to delete this order?')) {
        console.log("deleteOrder", id);
        this.$store.dispatch("deleteItem", { uri: 'orders', data: id });
      }
    },
    async logout() {
      await this.$auth.logout();
      this.$store.commit('setAuth', null);
    }
  },
  mounted() {
    this.form.subject = this.subjects[0];
    console.log(this.duration[Object.keys(this.duration)[0]]);
    console.log(Object.entries(this.duration)[0]);
    this.form.duration = {
      duration: Object.entries(this.duration)[0][0],
      price: Object.entries(this.duration)[0][1],
    };
    this.formProfile.email = this.$auth.user ? this.$auth.user.email : null;
    this.formProfile.phone = this.$auth.user ? this.$auth.user.phone.split(" ")[1] === undefined ? this.$auth.user.phone.split(" ")[0] : this.$auth.user.phone.split(" ")[1] : null;
    this.formProfile.first_name = this.$auth.user && this.$auth.user.name.split(" ")[0] ? this.$auth.user.name.split(" ")[0] : null;
    this.formProfile.last_name = this.$auth.user && this.$auth.user.name.split(" ")[1] ? this.$auth.user.name.split(" ")[1] : null;

    if (this.$auth.user.metadata) {
      this.formProfile.country = this.$auth.user.metadata.country;
      this.formProfile.profile_image = [];
      this.formProfile.profile_image.push(`${this.$auth.user.metadata.profile_image.secure_url}`);
      this.formProfile.type = this.$auth.user.metadata.userType;
      this.formProfile.emailNotifications = this.$auth.user.metadata.emailNotifications;
      this.formProfile.pushNotifications = this.$auth.user.metadata.pushNotifications;
    }

    if (this.$auth.user.email_verified_at === null) {
      this.$toast.error("Check Mail Verification", {
        theme: "outline",
        position: "bottom-center",
        duration: 15000
      });
    }
    console.log(this.$auth.user)
  }
}
</script>

